func testInventoryItemToManyAddOpInventoryLevels(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a InventoryItem
	var b, c, d, e InventoryLevel

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, inventoryItemDBTypes, false, strmangle.SetComplement(inventoryItemPrimaryKeyColumns, inventoryItemColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*InventoryLevel{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, inventoryLevelDBTypes, false, strmangle.SetComplement(inventoryLevelPrimaryKeyColumns, inventoryLevelColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*InventoryLevel{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddInventoryLevels(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.InventoryItemID {
			t.Error("foreign key was wrong value", a.ID, first.InventoryItemID)
		}
		if a.ID != second.InventoryItemID {
			t.Error("foreign key was wrong value", a.ID, second.InventoryItemID)
		}

		if first.R.InventoryItem != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.InventoryItem != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.InventoryLevels[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.InventoryLevels[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.InventoryLevels().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

