func testInventoryItemsSliceUpdateAll(t *testing.T) {
	t.Parallel()

	if len(inventoryItemAllColumns) == len(inventoryItemPrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &InventoryItem{}
	if err = randomize.Struct(seed, o, inventoryItemDBTypes, true, inventoryItemColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize InventoryItem struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := InventoryItems().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, inventoryItemDBTypes, true, inventoryItemPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize InventoryItem struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(inventoryItemAllColumns, inventoryItemPrimaryKeyColumns) {
		fields = inventoryItemAllColumns
	} else {
		fields = strmangle.SetComplement(
			inventoryItemAllColumns,
			inventoryItemPrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := InventoryItemSlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
}

